import 'next-auth';
import 'next-auth/jwt';

declare module 'next-auth' {
  interface User {
    id: string;
    email: string;
    name?: string;
    role: 'admin' | 'coach' | 'user';
    shopDomain?: string;
    twoFactorEnabled?: boolean;
  }

  interface Session {
    user: {
      id: string;
      email: string;
      name?: string;
      role: 'admin' | 'coach' | 'user';
      shopDomain?: string;
      twoFactorEnabled?: boolean;
    };
    accessTokenExpired?: boolean;
  }
}

declare module 'next-auth/jwt' {
  interface JWT {
    id: string;
    role: 'admin' | 'coach' | 'user';
    shopDomain?: string;
    twoFactorEnabled?: boolean;
    accessTokenIssuedAt?: number;
    accessTokenExpired?: boolean;
  }
}
